app.controller("LoginCtrl",["$scope","$http","$window","$location",function(e,s,o,a){e.user={username:null,password:null},e.message="",e.auth=function(){s.post("http://ec2-54-204-155-167.compute-1.amazonaws.com/api/auth-token/",e.user).success(function(e){o.localStorage.token=e.token,a.path("/account")}).error(function(s){if(delete o.localStorage.token,s.username)e.message="Please enter a username.";else if(s.password)e.message="Please enter a password.";else for(first in s)e.message=s[first][0]})}}]);